"use strict";

var btnScrollTop = document.querySelector('.footer__btn-scroll');
var header = document.querySelector('.header');
btnScrollTop.addEventListener('click', function () {
  header.scrollIntoView({
    block: "center",
    behavior: "smooth"
  });
});

//burger menu

var iconMenu = document.querySelector(".menu__icon");
var iconMenuSpan = document.querySelector(".menu__icon > span");
var menuBody = document.querySelector(".header__nav");
var body = document.querySelector('body');
iconMenu.addEventListener("click", function (e) {
  iconMenu.classList.toggle("_active");
  menuBody.classList.toggle("_active");
  body.classList.toggle('overflow');
});

//footer menu
if (window.innerWidth <= 960) {
  var footerItemTitle = document.querySelectorAll('.footer__item');
  footerItemTitle.forEach(function (item) {
    item.addEventListener('click', function () {
      item.classList.toggle('_open');
      var menu = item.querySelector('.footer__links-list');
      menu.classList.toggle('_open');
    });
  });
}
